const swiper=new Swiper(".mySwiper",{spaceBetween:30,navigation:{nextEl:".button-next",prevEl:".button-prev"},breakpoints:{}}),questionsList=document.querySelectorAll(".questions__list");questionsList.forEach(s=>{const e=s.querySelector(".questions__list_header"),t=s.querySelector(".questions__list_body");e.addEventListener("click",()=>{s.classList.contains("questions__list_active")?(s.classList.remove("questions__list_active"),t.classList.remove("questions__body_active")):(s.classList.add("questions__list_active"),t.classList.add("questions__body_active"))})});